import Posts from "../components/Posts/Posts";

export default (post = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;
        case 'CREATE':
            return [...Posts, action.payload];
        case 'UPDATE':
            return Posts.map((post) => post._id === action.payload._id? action.payload:post);
        default:
            return post;
    }
}
