import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Avatar, Button, Paper, Grid, Typography, Container } from '@material-ui/core';
import { useHistory } from 'react-router-dom';
//import { GoogleLogin } from 'react-google-login';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Input from './Input';
//import Icon from './Icon';
import useStyles from './styles';
import {signin, signup} from '../../actions/auth.js';
const initialState = {firstName:'', lastName:'', email:'', password:'', confirmPassword:''};
const SignUpp = () => {
    const classes = useStyles();
    const [isSignUp, setIsSignup] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState(initialState);
    const dispatch = useDispatch();
    const history = useHistory();
    const handleSubmit = (e) => {
        e.preventDefault()

        if (isSignUp) {
            dispatch(signup(formData, history));
        } else {
            dispatch(signin(formData, history));
        }

    };
    const handleChange = (e) => {
        setFormData({...formData, [e.target.name]:e.target.value});

    };
    const handleShowPassword = () => setShowPassword(
        (prevShowPassword) => !prevShowPassword
    );

    const switchStatus = () => {
        //setForm(initialState);
        setIsSignup((prevIsSignup) => !prevIsSignup);
        setShowPassword(false);
      };

    // const googleSuccess = async (res) => {
    //     const result = res?.profileObj;
    //     const token = res?.tokenId;
    //     try {
    //         dispatch({ type: "AUTH", data: { result, token } });
    //         history.push('/');
    //     } catch (error) {
    //         console.log(error);
    //     }
    // };

    // const googleError = () => {
    //     alert('Google Sign In was unsuccessful. Try again later')
    // };
  return (
    <Container component="main" maxWidth="xs">
        <Paper className={classes.paper} elevation={3}>
            <Avatar className={classes.avatar}>
                <LockOutlinedIcon />
            </Avatar>
            <Typography variant="h5">{isSignUp? 'Sign Up': 'Sign In'}</Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid container spacing={2}>

                    {isSignUp && (
                        <>  
                            <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half/>
                            <Input name="lastName" label="Last Name" handleChange={handleChange} autoFocus half/>
                        </>
                    )}
                    <Input name="email" label="Email Address" handleChange={handleChange} type="email"/>
                    <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? 'text' : 'password'} handleShowPassword={handleShowPassword}/>
                    {isSignUp && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password"/>}
                </Grid>    


                <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
                    {isSignUp ? 'Sign Up':'Sign In'}
                </Button>

                {/* <GoogleLogin
                clientId="745638954834-uh6pknec38frfnros7bi3338vk0h0odl.apps.googleusercontent.com"
                render={(renderProps) => (
                    <Button 
                    className={classes.googleButton} 
                    color="primary" 
                    fullWidth 
                    onClick={renderProps.onClick}
                    disabled={renderProps.disabled}
                    startIcon={<Icon/>}
                    variant="contained"
                    > Google Sign In  
                    </Button>
                )}
                onSuccess={googleSuccess}
                onFailure={googleError}
                cookiePolicy="single_host_origin"
                /> */}

                <Grid container justifyContent='flex-end'>
                    <Grid item>
                        <Button  color="secondary" onClick={switchStatus}>
                            {isSignUp? 'If you already have an account, sign in please' : 'Don\'t have an account? Sign up here'}
                        </Button>
                    </Grid>
                </Grid>               
            </form>
        </Paper>
    </Container>
  )
}
export default SignUpp;
