import React, {useState, useRef} from 'react';
import { Typography, Button, TextField } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import useStyles from './styles';
import commentPost from '../../actions/posts.js';
const commentSection = () => {
    console.log('Comment Section');
    classes = useStyles();
    const [comments, setComments] = useState(post?.comments);
    const [comment, setComment] = useState('');
    const user = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch();
    const commentsRef = useRef();
    const handleClick = async () => {
        const usersComment = `${user.result.name}: ${comment}`;
        const newComments = await dispatch(commentPost(usersComment, post._id));
        setComments(newComments);
        setComment("");
        commentsRef.current.scrollIntoView({behavior:'smooth'});
    }
    return (
        <div>
            <div className={classes.classOuterContainer}>
                <div className={classes.classes.classInnerContainer}>
                    <Typography gutterBottom variant="h6">Comments</Typography>
                    {comments.map((c, i) => (
                        <Typography key={i} gutterBottom variant="subtitle1">
                            {c}
                        </Typography>
                    ))}
                    <div ref={commentsRef} />
                </div>
                {user?.result?.name && (
                    <div style={{width: '70%'}}>
                    <Typography gutterBottom variant="h6">Write a Comment</Typography>
                        <TextField 
                        fullWidth 
                        rows={4} 
                        variant="outlined" 
                        label="Comment" 
                        multiline 
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}></TextField>
                        <Button style={{marginTop: '10px'}} 
                        fullWidth
                        disabled={!comment}
                        variant="contained"
                        color="primary"
                        onClick={handleClick}>
                            Comment
                        </Button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default commentSection;
