import React, {useState, useRef} from 'react';
import { Typography, Button, TextField } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import useStyles from './styles';
const commentSection = () => {
    console.log('Comment Section');
    classes = useStyles();
    const [comments, setComments] = useState([]);
    const [comment, setComment] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));
    const dispatch = useDispatch();
    const handleClick = () => {
        const usersComment = `${user.result.name}: ${comment}`;
        dispatch(commentPost(usersComment, post._id));
    }
    return (
        <div>
            <div className={classes.classOuterContainer}>
                <div className={classes.classes.classInnerContainer}>
                    <Typography gutterBottom variant="h6">Comments</Typography>
                    {comments.map((c, i) => (
                        <Typography key={i} gutterBottom variant="subtitle1">
                            Comment {i}
                        </Typography>
                    ))}
                </div>
                <div style={{width: '70%'}}>
                <Typography gutterBottom variant="h6">Write a Comment</Typography>
                    <TextField 
                    fullWidth 
                    rows={4} 
                    variant="outlined" 
                    label="Comment" 
                    multiline 
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}></TextField>
                    <Button style={{marginTop: '10px'}} 
                    fullWidth
                    disabled={!comment}
                    variant="contained"
                    color="primary"
                    onClick={handleClick}>
                        Comment
                    </Button>
                </div>
            </div>
        </div>
    );
}

export default commentSection;
